const sendmail = require('sendmail')();

export default async (req, res) => {
  const form = req.body;
  const url = `/Users/danmurciano/Web-Dev/Projects/Jose/public/${form.photo}`

  sendmail({
    from: form.email,
    to: 'danm.editing@gmail.com',
    subject: 'Website Request',
    html: form.message,
    attachments: [
      {
        filename: 'photo.png',
        path: url
      }
    ]
  }, function(err, reply) {
    console.log(err && err.stack);
    console.dir(reply);
  });

  res.status(200).json(form);

}
