import React from "react";
import PhotoIndex from "../components/Gallery/PhotoIndex";
import PicturePagination from "../components/Gallery/IndexPagination";
import ContactModal from "../components/Gallery/ContactModal";
import { portrait } from "../public/portrait";
import { photoList } from "../public/photoList";
import { Label, Button, Modal } from 'semantic-ui-react';


export default function Home() {
  const [ page, setPage ] = React.useState(photoList[0]);
  const [ indexPage, setIndexPage ] = React.useState(1);
  const [ contactModal, setContactModal] = React.useState(false);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = photoList.length;
  const totalPages = Math.ceil(totalPictures / pageSize);


  return (
      <div class="pageGallery">

        <div class="pictureTable">
          <img src={`/photos_good/portrait_edit/${page.number}.png`} class="photoMain" alt="..."/>

          <div class="photoBottom">
            <p className="photoInfo"> Acrylic &nbsp;{page.size} </p>
            <Button
              size="small"
              className="availability-button"
              onClick={() => setContactModal(true)}
            >
              Check Availability
            </Button>
          </div>
        </div>

        <Modal
          className="contactModal"

          centered
          closeIcon
          open={contactModal}
          onClose={() => setContactModal(false)}
          onOpen={() => setContactModal(true)}
        >
          <ContactModal
            setContactModal={setContactModal}
            page={page.number}
          />
        </Modal>


        <div class="indexSection">

          <PhotoIndex
            page={page}
            setPage={setPage}
            indexPage={indexPage}
            totalPages={totalPages}
          />


          <div class="paginationRow">
            <PicturePagination
              indexPage={indexPage}
              setIndexPage={setIndexPage}
              totalPages={totalPages}
            />
          </div>

        </div>

      </div>

  );

}
