import React, { useEffect, useState } from "react";
import Nav from 'react-bootstrap/Nav';
// import { useRouter } from 'next/router';
import { portrait } from "../../public/portrait";

function PhotoIndex({ page, setPage, indexPage }) {
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (indexPage === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (indexPage - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  function isActive(photo) {
    if (photo === page) {
      return true;
    }
  }

  function handlePhotoSelect(photo) {
    setPage(photo);
  }

  function mapProductsToItems(pictures) {
    return pictures.map(photo => (

      <div class="col">
        <Nav.Link
          active={isActive(photo)}
          onSelect={handlePhotoSelect()}
          eventKey={photo}
        />
          <img
            id={photo}
            src={`/photos_good/portrait_edit/${photo}.png`}
            class="card-img-top indexThumbnail"
            alt="..."
            // onClick={() => handlePhotoSelect(photo)}
          />
        <Nav.Link/>
      </div>
    ));
  }

  return (
    <div class="photoIndexRow">
      <div class="row photoIndex">
        <Nav activeKey="60" onSelect={handlePhotoSelect()}>
          {mapProductsToItems(pictures)}
        </Nav>
      </div>
    </div>
  );
}

export default PhotoIndex;
