import React, { useEffect, useState } from "react";
import { portrait } from "../../public/portrait";

function PhotoIndex(page, setPage, indexPage) {
  console.log(indexPage);
  // const [ page, setPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (indexPage === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (indexPage - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };

  console.log(pictures, indexPage, "hy");

  function handlePhotoSelect(page) {
    setPage(page);
    // data.activePage === 1
    //   ? router.push("/")
    //   : router.push(`/?page=${data.activePage}`)
  }

  function mapProductsToItems(pictures) {
    return pictures.map(picture => (

      <div class="col">
        <a href="">
          <img src={`/photos_good/portrait_edit/${picture}.png`} class="card-img-top indexThumbnail" alt="..."/>
        </a>
      </div>
    ));
  }

  return (
    <div class="photoIndexRow">
      <div class="row photoIndex">
        {mapProductsToItems(pictures)}
      </div>
    </div>
  );
}

export default PhotoIndex;
