import React, { useEffect, useState } from "react";
import { Carousel, Container, Row } from  "react-bootstrap";

function PictureCarousel() {
  const [index, setIndex] = React.useState(0);

  const handleSelect = (selectedIndex, e) => {
    setIndex(selectedIndex);
  };

  function mapProductsToItems(portrait) {
    console.log(portrait);
    return portrait.map(picture => (

      <div class="col-12 col-md-6 col-lg-4 col-xl-4 productCard">
        <a class="productLink" href={""}>
          <img src={`/photos_good/portrait_edit/${picture}.png`} class="card-img-top indexProductImage" alt="..."/>
        </a>
      </div>
    ));
  }

  return (
    <Carousel activeIndex={index} onSelect={handleSelect}>
      <Carousel.Item>
        <Container className="pictureTable">
          <Row>
            {mapProductsToItems(portrait)}
          </Row>
        </Container>
      </Carousel.Item>

    </Carousel>
  );
}

export default PictureCarousel;
