import React from "react";
import PhotoMain from "../components/Gallery/PhotoMain";
import PhotoIndex from "../components/Gallery/PhotoIndex";
import IndexPagination from "../components/Gallery/IndexPagination";
import { photoList } from "../public/photoList";
import { Button, Modal, Reveal } from 'semantic-ui-react';
import { CSSTransition } from 'react-transition-group';


export default function Gallery() {
  const [ page, setPage ] = React.useState(photoList[0]);
  const [ prevPage, setPrevPage ] = React.useState("");
  const [ indexPage, setIndexPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = photoList.length;
  const totalPages = Math.ceil(totalPictures / pageSize);


  return (
    <div class="pageGallery">

      <CSSTransition in={page} timeout={500} classNames="my-node">
        <div class="pictureTable fade-in">
          <PhotoMain
            page={page}
            prevPage={prevPage}
          />
          <PhotoMain
            page={prevPage}
            prevPage={prevPage}
          />
        </div>
      </CSSTransition>


      <div class="indexSection">

        <PhotoIndex
          page={page}
          setPage={setPage}
          prevPage={prevPage}
          setPrevPage={setPrevPage}
          indexPage={indexPage}
          totalPages={totalPages}
        />


        <div class="paginationRow">
          <IndexPagination
            indexPage={indexPage}
            setIndexPage={setIndexPage}
            totalPages={totalPages}
          />
        </div>

      </div>

    </div>
  );
}
