// import React from "react";
import { Navbar, Nav, Form, FormControl } from "react-bootstrap";
import { Menu, Container, Image, Icon } from "semantic-ui-react";
import Link from "next/link";
import Router, { useRouter } from "next/router";


function Header() {
  const router = useRouter();

  function isActive(route) {
    return route === router.pathname;
  }

  return (
    <>
    <div class="title">
      <p class="title"> Jose Murciano </p>
    </div>

    <Menu stackable fluid id="menu">
      <Container text className="navbarCenter">
        <Link href="/">
          <Menu.Item header active={isActive("/")}>
            <p class="navText">Home</p>
          </Menu.Item>
        </Link>

        <Link href="/">
          <Menu.Item header active={isActive("/gallery")}>
            <p class="navText">Gallery</p>
          </Menu.Item>
        </Link>

        <Link href="/">
          <Menu.Item header active={isActive("/contact")}>
            <p class="navText">Contact</p>
          </Menu.Item>
        </Link>

    </Container>
  </Menu>
      </>

  );
}

export default Header;
