import React from 'react';
import { useRouter } from "next/router";
import { Grid, Pagination } from 'semantic-ui-react';

function PicturesPagination({ setPage, totalPages }) {
  const router = useRouter();

  function handlePageChange(event, data) {
    setPage(data.activePage);
    data.activePage === 1
      ? router.push("/")
      : router.push(`/?page=${data.activePage}`)
  }

  return (
    <Grid.Column>
    <Pagination
      class={uiPaginationMenu}
      boundaryRange={0}
      defaultActivePage={1}
      ellipsisItem={null}
      firstItem={null}
      lastItem={null}
      siblingRange={1}
      totalPages={totalPages}
      onPageChange={handlePageChange}
    />
    </Grid.Column>
  );
}


export default PicturesPagination;
