import React from "react";
// import PictureCarousel from "../components/Index/PictureCarousel";
import { portrait } from "../public/portrait";

export default function Home() {

  let pictures = [];

  function picturesToPages(pictures, page) {
    const pageNum = Number(page);
    const pageSize = 6;
    const totalPictures = portrait.length;
    const totalPages = Math.ceil(totalPictures / pageSize);
    if (pageNum === 1) {
      pictures = portrait.find().limit(pageSize);
    } else {
      const skips = pageSize * (pageNum - 1);
      pictures = portrait.find().skip(skips).limit(pageSize);
    };
    return { pictures, totalPages };
  };


  function mapProductsToItems(pictures) {
    return pictures.map(picture => (

      <div class="col-12 col-md-6 col-lg-4 col-xl-4 productCard">
        <a class="productLink" href={""}>
          <img src={`/photos_good/portrait_edit/${picture}.png`} class="card-img-top indexProductImage" alt="..."/>
        </a>
      </div>
    ));
  }

  return (
    <div>
      <div class="pageHome">
       <h3> </h3>
        <div class="pictureTable">
          <div class="row">
            {mapProductsToItems(pictures)}
          </div>
        </div>
      </div>
    </div>

  );
}
