import React from "react";
import ContactModal from "./ContactModal";
import { photoList } from "../../public/photoList";
import { Label, Button, Modal } from 'semantic-ui-react';


export default function PhotoMain({ pageA, pageB, activePage }) {
  const [ contactModal, setContactModal] = React.useState(false);

  return (
    <>
    <div class="pictureTable">
    <div class={activePage === "A" ? "top" : "bottom"} >
      <img src={`/images/full_size/${pageA.number}.png`} class="photoMain" alt="..." />
    </div>

    <div class={activePage === "B" ? "top" : "bottom"} >
      <img src={`/images/full_size/${pageB.number}.png`} class="photoMain" alt="..." />
    </div>
    </div>

    <div class="photoBottom">
      <p className="photoInfo"> Acrylic &nbsp;{pageA.size} </p>
      <Button
        size="small"
        className="availability-button"
        onClick={() => setContactModal(true)}
      >
        Check Availability
      </Button>
    </div>

    <Modal
      className="contactModal w3-animate-zoom"
      dimmer="blurring"
      centered
      closeIcon
      open={contactModal}
      onClose={() => setContactModal(false)}
      onOpen={() => setContactModal(true)}
    >
      <ContactModal
        setContactModal={setContactModal}
        page={pageA.number}
      />
    </Modal>
    </>

  );
}
