import React from "react";
import PhotoIndex from "../components/Index/PhotoIndex";
import PicturePagination from "../components/Index/PicturesPagination";
import ContactModal from "../components/Index/ContactModal";
import { portrait } from "../public/portrait";
import { photoList } from "../public/photoList";
import { Label, Button, Modal } from 'semantic-ui-react';


export default function Home() {
  const [ page, setPage ] = React.useState(60);
  const [ indexPage, setIndexPage ] = React.useState(1);
  const [ contactModal, setContactModal] = React.useState(false);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = photoList.length;
  const totalPages = Math.ceil(totalPictures / pageSize);


  if (page === 1) {
    pictures = photoList.slice(0, pageSize);
  } else {
    const skips = pageSize * (page - 1);
    pictures = photoList.slice(skips, skips + pageSize);
  };


  return (

      <div class="pageHome">

        <div class="pictureTable">
          <img src={`/photos_good/portrait_edit/${page}.png`} class="card-img-top photoMain" alt="..."/>

          <div class="photoBottom">
            <p className="photoInfo"> {`Acrylic &nbsp; ${photoList[page].size}`} </p>
            <Button size="small" color='teal' horizontal className="photoStatus" onClick={() => setContactModal(true)}>
              Check Availability
            </Button>
          </div>
        </div>

        <Modal
          className="contactModal"
          dimmer="blurring centered"
          centered
          closeIcon
          open={contactModal}
          onClose={() => setContactModal(false)}
          onOpen={() => setContactModal(true)}
        >
          <ContactModal
            setContactModal={setContactModal}
            page={page}
          />
        </Modal>


        <div class="indexSection">

          <PhotoIndex
            page={page}
            setPage={setPage}
            indexPage={indexPage}
            totalPages={totalPages}
          />


          <div class="paginationRow">
            <PicturePagination
              indexPage={indexPage}
              setIndexPage={setIndexPage}
              totalPages={totalPages}
            />
          </div>

        </div>

      </div>

  );

}
