import React from "react";
import { Button, Form, Icon, Message, Segment } from "semantic-ui-react";
import Link from "next/link";
import axios from "axios";


export default function ContactModal({ setContactModal, photo }) {
  const [form, setForm] = React.useState( {message: "Is this painting available?", photo: {photo} });
  const [disabled, setDisabled] = React.useState(true);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState("");
  const [success, setSuccess] = React.useState(false);



  function handleChange(event) {
    const { name, value } = event.target;
    setForm(prevState => ({ ...prevState, [name]: value }));
  }

  async function handleSubmit(event) {
    event.preventDefault();
    try {
      setLoading(true);
      setError("");
      const url = `/api/contact`;
      const payload = { ...form };
      console.log(payload);
      const response = await axios.post(url, payload);
      setSuccess(true);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div class="pageContact">

      {success ? (

        <div class="formSuccess">
          <p> <Icon name="check" color="teal" size="large" /> </p>
          <p> Message sent. </p>
          <p> Thank you. </p>
        </div>

      ) : (

        <>
          <h1> Check Availability </h1>

          <div>
            <img src={`/photos_good/portrait_edit/${photo}.png`} class="card-img-top photoModal" alt="..."/>
          </div>


          <div class="contactForm">

            <Form onSubmit={handleSubmit} >
              <Form.Input
                required
                className="contactField"
                label="Name"
                placeholder="Name"
                name="name"
                value={form.name}
                onChange={handleChange}

              />
              <Form.Input
                required
                className="signInInput"
                label="Email"
                placeholder="Email"
                name="email"
                type="email"
                value={form.email}
                onChange={handleChange}
              />
              <Form.Field
                className="signInInput"
                label="Message"
                control="textarea"
                rows="10"
                name="message"
                value={form.message}
                onChange={handleChange}
              />

              <div class="submitForm">
                <Button
                  type="submit"
                  color="teal"
                  content="Submit"
                  size="large"
                  disabled={success}
                />
              </div>

          </Form>

        </div>

      </>
    )}

    </div>
  )
}
