import Nav from 'react-bootstrap/Nav';
// import { useRouter } from 'next/router';
import { portrait } from "../../public/portrait";

function PhotoIndex({ page, setPage, indexPage }) {
  const [ activeThumbnail, setActiveThumbnail ] = React.useState();
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (indexPage === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (indexPage - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  function isActive(photo) {
    if (photo === page) {
      return true;
    }
  }

  function handlePhotoSelect(photo, id) {
    setPage(photo);
    setActiveThumbnail(id)
