import React, { Component } from "react";
import ReactDOM from "react-dom";
import CrossfadeImage from "react-crossfade-image";

const images = [
  `/images/full_size/${59}.png`,
  `/images/full_size/${58}.png`
];

class PhotoFade extends Component {
  constructor() {
    super();
    this.state = {
      imageIndex: 0
    };
    this.changeImage = this.changeImage.bind(this);
  }
  changeImage() {
    if (this.state.imageIndex === images.length - 1) {
      this.setState({ imageIndex: 0 });
    } else {
      this.setState({ imageIndex: this.state.imageIndex + 1 });
    }
  }
  render() {
    return (
      <div>
        <CrossfadeImage
          src={images[this.state.imageIndex]}
          duration={1000}
          timingFunction={"ease-out"}
        />
        <button onClick={this.changeImage}>Change Image</button>
      </div>
    );
  }
}

export default PhotoFade;
