import React, { useEffect, useState } from "react";
import Nav from 'react-bootstrap/Nav';
// import { useRouter } from 'next/router';
import { portrait } from "../../public/portrait";

function PhotoIndex({ page, setPage, indexPage }) {
  const [ activeThumbnail, setActiveThumbnail ] = React.useState();
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (indexPage === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (indexPage - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  function isActive(photo) {
    if (photo === page) {
      return true;
    }
  }

  function handlePhotoSelect(photo, id) {
    setPage(photo);
    setActiveThumbnail(id)
    console.log(activeThumbnail);
  }

  function mapProductsToItems(pictures) {
    return pictures.map(photo => (

      <div class="col">
          <img
            id={`thumb_${photo}`}
            src={`/photos_good/portrait_edit/${photo}.png`}
            alt="..."
            onClick={() => handlePhotoSelect(photo, event.target.id)}
            class={this.id === activeThumbnail ?
              "card-img-top indexThumbnail" :
              "card-img-top indexThumbnailActive" }
          />
      </div>
    ));
  }

  return (
    <div class="photoIndexRow">
      <div class="row photoIndex">
        {mapProductsToItems(pictures)}
      </div>
    </div>
  );
}

export default PhotoIndex;
