import React from "react";
import PhotoIndex from "../components/Index/PhotoIndex";
import PicturePagination from "../components/Index/PicturesPagination";
import { portrait } from "../public/portrait";
import { Label, Button } from 'semantic-ui-react';


export default function Home() {
  const [ page, setPage ] = React.useState(60);
  const [ indexPage, setIndexPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);


  if (page === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (page - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  return (

      <div class="pageHome">

        <div class="pictureTable">
          <img src={`/photos_good/portrait_edit/${page}.png`} class="card-img-top photoMain" alt="..."/>

          <div class="photoBottom">
            <p className="photoInfo"> Acrylic &nbsp; 70cm x 50cm </p>
            <Button size="small" color='teal' horizontal className="photoStatus">
              Check Availability
            </Button>
          </div>
        </div>

        <div>

        <div class="indexSection">
          <PhotoIndex
            page={page}
            setPage={setPage}
            indexPage={indexPage}
            totalPages={totalPages}
          />
        </div>

        <div class="paginationRow">
          <PicturePagination
            indexPage={indexPage}
            setIndexPage={setIndexPage}
            totalPages={totalPages}
          />
        </div>

        </div>

      </div>

  );

}
