// import React from "react";
import { Navbar, Nav, Form, FormControl } from "react-bootstrap";
import Link from "next/link";
import Router, { useRouter } from "next/router";


function Header() {
  const router = useRouter();
  const [ activePage, setActivePage ] = React.useState("index");

  function isActive(page) {
    if (page === router.pathname) {
      return true;
    }
  }

  function handlePageSelect(id) {
    // setPage(page);
    setActivePage(id)
  }


  return (
    <>
    <div class="title">
      <p class="title"> Jose Murciano </p>
    </div>

    <nav class="navbar navbar-expand-lg navbar-dark navbarCenter">

      <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#nav-menu" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
        <span class="navbar-toggler-icon"></span>
      </button>

      <div class="collapse navbar-collapse" id="nav-menu">

        <ul class="navbar-nav navbarList">
            <li class="nav-item">
              <a class="nav-link" href="/">
                <p
                  id="/"
                  onClick={() => handlePageSelect(event.target.id)}
                  class={activePage ?
                    "navTextActive" :
                    "navText" }
                >Home</p>
              </a>
            </li>

            <li class="nav-item">
              <a class="nav-link" href="/gallery">
                <p
                  id="gallery"
                  onClick={() => handlePageSelect(event.target.id)}
                  class={activePage === "gallery" ?
                    "navTextActive" :
                    "navText" }
                >Gallery</p>
              </a>
            </li>

            <li class="nav-item">
              <a class="nav-link" href="/contact">
                <p class="navText">Contact</p>
              </a>
            </li>
        </ul>

      </div>
    </nav>
      </>

  );
}

export default Header;
