import React from "react";
import { useRouter } from 'next/router'
import PhotoIndex from "../components/Index/PhotoIndex";
import PicturePagination from "../components/Index/PicturesPagination";
import { portrait } from "../public/portrait";


export default function Gallery() {
  const router = useRouter();
  const { pid } = router.query;
  const [ page, setPage ] = React.useState(60);
  const [ indexPage, setIndexPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 1;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (page === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (page - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  function mapProductsToItems(page) {
    return (
      <div class="productCard">
        <img src={`/photos_good/portrait_edit/${page}.png`} class="card-img-top indexProductImage2" alt="..."/>
      </div>
    );
  }

  return (
    <div>
      <div class="pageHome">
        <div class="productCard pictureTable">
          <img src={`/photos_good/portrait_edit/${page}.png`} class="card-img-top indexProductImage2" alt="..."/>
        </div>

        <div>
          <PhotoIndex
            page={page}
            setPage={setPage}
            indexPage={indexPage}
            totalPages={totalPages}
          />
        </div>

        <div class="pagination">
          <PicturePagination
            setPage={setPage}
            totalPages={totalPages}
          />
        </div>

      </div>
    </div>
  );
}
