import React, { useEffect, useState } from "react";
import { portrait } from "../public/portrait";

function PhotoIndex() {
  const [ page, setPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 8;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (page === 1) {
    pictures = portrait.slice(0, pageSize);
  } else {
    const skips = pageSize * (page - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };

  function mapProductsToItems(portrait) {
    return portrait.map(picture => (

      <div>
        <a class="productLink" href={""}>
          <img src={`/photos_good/portrait_edit/${picture}.png`} class="card-img-top indexThumbnail" alt="..."/>
        </a>
      </div>
    ));
  }

  return (
    <div class="photoIndex">
      {mapProductsToItems(portrait)}
    </div>
  );
}

export default PhotoIndex;
