import React from "react";
import PicturePagination from "../components/Index/PicturesPagination";
import { portrait } from "../public/portrait";


export default function Home() {
  const [ page, setPage ] = React.useState(1);
  let pictures = [];
  const pageSize = 6;
  const totalPictures = portrait.length;
  const totalPages = Math.ceil(totalPictures / pageSize);

  if (page === 1) {
    pictures = portrait.slice(0, pageSize);
    console.log(pictures);
  } else {
    const skips = pageSize * (page - 1);
    pictures = portrait.slice(skips, skips + pageSize);
  };


  function mapProductsToItems() {
    return pictures.map(picture => (

      <div class="col-12 col-md-6 col-lg-4 col-xl-4 productCard">
        <a class="productLink" href={""}>
          <img src={`/photos_good/portrait_edit/${picture}.png`} class="card-img-top indexProductImage" alt="..."/>
        </a>
      </div>
    ));
  }

  return (
    <div>
      <div class="pageHome">
        <div class="pictureTable">
          <div class="row">
            {mapProductsToItems(pictures)}
          </div>
          <div class="pagination">
            <PicturePagination
              setPage={setPage}
              totalPages={totalPages}
            />
          </div>
        </div>
      </div>
    </div>

  );
}
